<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class AuthController extends CI_Controller {    public $project;    public function __construct() {        parent::__construct();        $this->load->model('Common_model');        $this->load->model('Finane_Model');        $this->load->model('Inventory_Model');        $this->load->model('Sales_Model');        $thisdis = $this->session->userdata('dis_id');        $admin_id = $this->session->userdata('admin_id');        if (!empty($thisdis) && !empty($admin_id)) {            redirect(site_url('DistributorDashboard'));        }        $this->project = $this->session->userdata('project');        $this->db_hostname = $this->session->userdata('db_hostname');        $this->db_username = $this->session->userdata('db_username');        $this->db_password = $this->session->userdata('db_password');        $this->db_name = $this->session->userdata('db_name');        $this->db->close();        $config_app = switch_db_dinamico($this->db_username, $this->db_password, $this->db_name);        $this->db = $this->load->database($config_app, TRUE);    }}