reach; ?>                            </select>                        </div>                    </div>                    <!--<div class="form-group">                <label class="col-sm-3 control-label no-padding-right" for="product_type_id"><?php /*echo get_phrase('Product Type')*/?><span style="color:red!important"> *</span></label>                <div class="col-sm-6">                    <select  id="product_type" name="product_type_id"  class="chosen-select form-control"  data-placeholder="Search Group Name" required="">                        <option value="" disabled="" selected=""> Select Product Type </option>                        <?php /*foreach ($product_type_list as $each_info): */?>                            <option  value="<?php /*echo $each_info->product_type_id; */?>"><?php /*echo $each_info->product_type_name; */?></option>                        <?php /*endforeach; */?>                    </select>                </div>            </div>-->                    <div class="form-group">                        <label class="col-sm-3 control-label no-padding-right" for="form-field-1"><?php echo get_phrase('Model Name')?><span style="color:red!important"> *</span></label>                        <div class="col-sm-6">                            <input type="text" id="productName" onblur="checkDuplicateProduct()" name="productName"  value="" class="form-control" placeholder="Product Model Name" />                            <span id="errorMsg"  style="color:red;display: none;"><i class="ace-icon fa fa-spinner fa-spin orange bigger-120"></i> &nbsp;&nbsp;This product name already exits!!</span>                        </div>                    </div>                    <div class="form-group">                        <label class="col-sm-3 control-label no-padding-right" for="form-field-1"> <?php echo get_phrase('Purchases Price')?><span style="color:red!important"> *</span></label>                        <div class="col-sm-6">                            <input required="required" type="text" id="purchases_price" name="purchases_price" oninput="this.value = this.value.replace(/[^0-9.]/g, ''); this.value = this.value.replace(/(\..*)\./g, '$1');" value="" class="form-control" placeholder="Purchases Price" />                        </div>                    </div>                    <div class="form-group">                        <label class="col-sm-3 control-label no-padding-right" for="form-field-1"> <?php echo get_phrase('Retail_Price_Mrp')?> <span style="color:red!important"> *</span></label>                        <div class="col-sm-6">                            <input required="required" type="text" id="salesPrice" name="salesPrice"  oninput="this.value = this.value.replace(/[^0-9.]/g, ''); this.value = this.value.replace(/(\..*)\./g, '$1');" value="" class="form-control" placeholder="Sales Price" />                        </div>                    </div>                    <div class="form-group">                        <label class="col-sm-3 control-label no-padding-right" for="form-field-1"> <?php echo get_phrase('Whole_Sale_Price')?><span style="color:red!important"> *</span></label>                        <div class="col-sm-6">                            <input required="required" type="text" id="retailPrice" name="retailPrice"  oninput="this.value = this.value.replace(/[^0-9.]/g, ''); this.value = this.value.replace(/(\..*)\./g, '$1');"  value="" class="form-control" placeholder="Wholesale Price" />                        </div>                    </div>                    <div class="form-group">                        <label class="col-sm-3 control-label no-padding-right" for="form-field-1"> <?php echo get_phrase('Alarm Quantity')?></label>                        <div class="col-sm-6">                            <input type="text" id="form-field-1" name="alarm_qty"  oninput="this.value = this.value.replace(/[^0-9.]/g, ''); this.value = this.value.replace(/(\..*)\./g, '$1');"  value="" class="form-control" placeholder="Alert Quantity" />                        </div>                    </div>                    <div class="clearfix form-actions" >                        <div class="col-md-offset-3 col-md-9">                            <button onclick="return isconfirmProduct2()"   id="subBtn" class="btn btn-info" type="button">                                <i class="ace-icon fa fa-check bigger-110"></i>                                <?php echo get_phrase('Save')?>                            </button>                            &nbsp; &nbsp; &nbsp;                            <button class="btn" type="reset">                                <i class="ace-icon fa fa-undo bigger-110"></i>                                <?php echo get_phrase('Reset')?>                            </button>                        </div>                    </div>                </form>                <!-- END FORM-->            </div>        </div>    </div></div><script>    function checkDuplicateProduct() {        var productCategory = $("#productCategory").val();        var brandId = $("#brandId").val();        var productName = $("#productName").val();        var url = '<?php echo site_url("lpg/InvProductController/checkDuplicateProduct"); ?>';        $.ajax({            type: 'POST',            url: url,            data: {'productName': productName, 'productCategory': productCategory, 'brandId': brandId},            success: function (data) {                if (data == 1) {                    $("#subBtn").attr('disabled', true);                    $("#errorMsg").show();                } else {                    $("#subBtn").attr('disabled', false);                    $("#errorMsg").hide();                }            }        });    }   function isconfirmProduct2() {        var productName = $("#productName").val();        var purchases_price = $("#purchases_price").val();        var salesPrice = $("#salesPrice").val();        var retailPrice = $("#retailPrice").val();        if (productName == '') {            swal("Give Product  Name!", "Validation Error!", "error");        }        /*else if (purchases_price == '') {            swal("Give Product Purchase  Price!", "Validation Error!", "error");        } else if (salesPrice == '') {            swal("Give Product Sales  Price!", "Validation Error!", "error");        } else if (retailPrice == '') {            swal("Give Whole Sale Price!", "Validation Error!", "error");        }*/        else {            swal({                    title: "Are you sure ?",                    text: "You won't be able to revert this!",                    showCancelButton: true,                    confirmButtonColor: '#73AE28',                    cancelButtonColor: '#d33',                    confirmButtonText: 'Yes',                    cancelButtonText: "No",                    closeOnConfirm: true,                    closeOnCancel: true,                    type: 'success'                },                function (isConfirm) {                    if (isConfirm) {                        $("#publicForm").submit();                    } else {                        return false;                    }                });        }    }</script>