   <div class="row">    <div class="col-md-12">        <div class="portlet box blue">            <div class="portlet-title" style="min-height:21px">                <div class="caption" style="font-size: 14px;padding:1px 0 1px;">                    <?php echo get_phrase('Supplier_Edit')?>   </div>                      </div>                      <div class="portlet-body">            <div class="row">                <div class="col-md-12">                    <form id="publicForm" action="" method="post" class="form-horizontal">                        <div class="form-group">                            <label class="col-sm-3 control-label no-padding-right" for="form-field-1"> <?php echo get_phrase('Supplier Id')?> <span                                        style="color:red;"> * </span> </label>                            <div class="col-sm-6">                                <input type="text" id="form-field-1" name="supplierId" readonly                                       value="<?php echo $supplierEdit->supID; ?>" class="form-control"                                       placeholder="SupplierID"/>                            </div>                        </div>                        <div class="form-group">                            <label class="col-sm-3 control-label no-padding-right" for="form-field-1"> <?php echo get_phrase('Supplier Name')?>                                <span style="color:red;"> * </span> </label>                            <div class="col-sm-6">                                <input type="text" id="form-field-1" name="supName"                                       value="<?php echo $supplierEdit->supName; ?>" class="form-control"                                       placeholder="Name"/>                            </div>                        </div>                        <div class="form-group">                            <label class="col-sm-3 control-label no-padding-right" for="form-field-1"> <?php echo get_phrase('Phone')?><span                                        style="color:red;"> * </span> </label>                            <div class="col-sm-6">                                <input type="text" id="form-field-1" value="<?php echo $supplierEdit->supPhone; ?>"                                       oninput="this.value = this.value.replace(/[^0-9.]/g, ''); this.value = this.value.replace(/(\..*)\./g, '$1');"                                       onblur="checkDuplicatePhone(this.value)" name="supPhone" placeholder="Phone"                                       class="form-control"/>                                <span id="errorMsg" style="color:red;display: none;"><i                                            class="ace-icon fa fa-spinner fa-spin orange bigger-120"></i> &nbsp;&nbsp;Phone Number already Exits!!</span>                            </div>                        </div>                        <div class="form-group">                            <label class="col-sm-3 control-label no-padding-right" for="form-field-1"> <?php echo get_phrase('Email')?></label>                            <div class="col-sm-6">                                <input type="text" id="form-field-1" name="supEmail"                                       value="<?php echo $supplierEdit->supEmail; ?>" placeholder="Email"                                       class="form-control"/>                            </div>                        </div>                        <div class="form-group">                            <label class="col-sm-3 control-label no-padding-right" for="form-field-1"> <?php echo get_phrase('Address')?><span                                        style="color:red;"> * </span> </label>                            <div class="col-sm-6">                                <textarea cols="6" rows="3" placeholder="Type Address.." class="form-control"                                          name="supAddress"><?php echo $supplierEdit->supAddress; ?></textarea>                            </div>                        </div>                        <div class="clearfix form-actions">                            <div class="col-md-offset-3 col-md-9">                                <button onclick="return confirmSwat()" id="subBtn" class="btn btn-info" type="button">                                    <i class="ace-icon fa fa-check bigger-110"></i>                                    <?php echo get_phrase('Update')?>                                </button>                                &nbsp; &nbsp; &nbsp;                                <button class="btn" type="reset">                                    <i class="ace-icon fa fa-undo bigger-110"></i>                                    <?php echo get_phrase('Reset')?>                                </button>                            </div>                        </div>                    </form>                </div>            </div><!-- /.col -->        </div><!-- /.row -->    </div><!-- /.page-content --></div></div><script src="<?php echo base_url(); ?>assets/js/bootstrap-colorpicker.min.js"></script><script>    function checkDuplicatePhone(phone) {        var url = '<?php echo site_url("SetupController/checkDuplicateEmail") ?>';        $.ajax({            type: 'POST',            url: url,            data: {'phone': phone},            success: function (data) {                if (data == 1) {                    $("#subBtn").attr('disabled', true);                    $("#errorMsg").show();                } else {                    $("#subBtn").attr('disabled', false);                    $("#errorMsg").hide();                }            }        });    }    $('#colorpicker1').colorpicker();</script>