e="">                        <div class="col-md-4">                            <div class="form-group" style="margin-left: -29px;">                                <label class="col-sm-3 control-label formfonterp" for="form-field-1" style="white-space: nowrap;">                                    <strong><?php echo get_phrase('Paid Amount') ?></strong></label>                                <div class="col-sm-7">                                    <input type="text" class="form-control  required decimal" autocomplete="off"                                           placeholder="Paid Amount" id="paid_amount" name="paid_amount" value=""                                           style="color: black" onclick="this.select();"/>                                </div>                            </div>                        </div>                    </div>                    <div class="clearfix"></div>                    <div class="row" style="margin-top: 10px;">                        <div class="col-sm-12">                            <div id="voucherheader" class="table-header" style="display: none;">                                <?php echo get_phrase('Due Voucher List') ?>                            </div>                            <span id="customer_result"></span>                        </div>                        <div class="col-sm-10 col-md-offset-1" id="narration" style="display: none;">                            <div class="form-group">                                <label class="col-sm-3 control-label no-padding-right"                                       for="form-field-1"> <?php echo get_phrase('Narration') ?></label>                                <div class="col-sm-9">                                    <div class="input-group">                                        <textarea cols="100" rows="2" name="narration" placeholder="Narration"                                                  type="text"></textarea>                                    </div>                                </div>                            </div>                        </div>                        <div class="col-sm-10 col-md-offset-1">                            <div class="clearfix form-actions" id="submitBtn" style="display: none;">                                <div class="col-md-offset-3 col-md-9">                                    <button onclick="return confirmWithValidat()" id="subBtn" class="btn btn-info"                                            type="button">                                        <i class="fa fa-check bigger-110"></i>                                        <?php echo get_phrase('Save') ?>                                    </button>                                    &nbsp; &nbsp; &nbsp;                                    <button class="btn" type="reset">                                        <i class="ace-icon fa fa-undo bigger-110"></i>                                        Reset                                    </button>                                </div>                            </div>                        </div>                    </div>                </form>            </div>        </div>    </div></div><script type="text/javascript">    function confirmWithValidat() {        var paymentType = $("input[name='payType']:checked").val();        var supplierId = $("#customerId").val();        var paymentType = $("#paymentType").val();        var paymentDate = $("#start_date").val();        var bankName = $("#bankName").val();        var branchName = $("#branchName").val();        var checkNo = $("#checkNo").val();        var checkDate = $("#checkDate").val();        var accountHead = $("#accountHead").val();        var ttl_amount = Number($(".ttl_amount ").val());        var paid_amount = parseFloat($("#paid_amount ").val());        var paidAmountSaparationn = 0;        $.each($('.amount'), function (i, elem) {            if ($(this).val() != '') {                paidAmountSaparationn = paidAmountSaparationn + parseFloat($(this).val());            }        })        if (isNaN(ttl_amount)) {            ttl_amount = 0;        }        if (supplierId == '') {            swal("Supplier Cnn't be empty!!", 'Validatin Error', 'error');        } else if (paymentDate == '') {            swal("Payment Date Cnn't be empty!!", 'Validatin Error', 'error');        } else if (paymentType == 2 && bankName == '') {            swal("Bank Name Can't be empty!!", 'Validatin Error', 'error');        }        /*else if (paymentType == 2 && branchName == '') {            swal("Branch Name Can't be empty!!", 'Validatin Error', 'error');        } */        else if (paymentType == 2 && checkNo == '') {            swal("Check No Can't be empty!!", 'Validatin Error', 'error');        } else if (paymentType == 2 && checkDate == '') {            swal("Check Date Can't be empty!!", 'Validatin Error', 'error');        } else if (paymentType == 1 && accountHead == '') {            swal("Account Head Can't be empty!!", 'Validatin Error', 'error');        } else if (paidAmountSaparationn != paid_amount) {            swal("Amount Can't Change!!");        }        else if (ttl_amount == '') {            swal("Amount Can't be empty!!");        } else {            swal({                    title: "Are you sure ?",                    text: "You won't be able to revert this!",                    showCancelButton: true,                    confirmButtonColor: '#73AE28',                    cancelButtonColor: '#d33',                    confirmButtonText: 'Yes',                    cancelButtonText: "No",                    closeOnConfirm: true,                    closeOnCancel: true,                    type: 'success'                },                function (isConfirm) {                    if (isConfirm) {                        $("#publicForm").submit();                    } else {                        return false;                    }                });        }    }    function checkOverAmount(dynamicId) {        var paymentAmount = parseFloat($("#paymentAmount_" + dynamicId).val());        if (isNaN(paymentAmount)) {            paymentAmount = 0;        }        var dueAmount = parseFloat($("#dueAmount_" + dynamicId).val());        if (isNaN(dueAmount)) {            dueAmount = 0;        }        if (paymentAmount > dueAmount) {            $("#paymentAmount_" + dynamicId).val('');        }        var totalVoucherPayment = 0;        $.each($('.amount'), function () {            voucherAmount = $(this).val();            if (isNaN(voucherAmount)) {                $(this).val('');                voucherAmount = 0;            }            voucherAmount = Number(voucherAmount);            totalVoucherPayment += voucherAmount;        });        if (totalVoucherPayment > 0) {            $("#subBtn").attr('disabled', false);        } else {            $("#subBtn").attr('disabled', true);        }    }    function showBankinfo(id) {        if (id == 1) {            $("#cashInfo").show(10);        } else {            $("#showBankInfo").hide(10);        }        if (id == 2) {            $("#showBankInfo").show(10);            $("#cashInfo").hide(10)        } else {            $("#showBankInfo").show(10)            $("#showBankInfo").hide(10);        }    }    function getBankBranch(id) {        $('#branchName').html('');        if (id != '') {            $.ajax({                type: "POST",                url: baseUrl + "lpg/SalesController/getbankbranchList",                data: 'bank_id=' + id,                dataType: 'json',                success: function (data) {                    console.log(data);                    var option = "";                    option += "<option value='" + '' + "'>---Select Name---</option>";                    $.each(data, function (key, value) {                        option += "<option   value='" + value.bank_branch_id + "'  >" + value.bank_branch_name + "</option>";                    });                    $('#branchName').html(option);                }            });        }    }    $(document).ready(function () {        $('.customerId').change(function () {            get_invoice_list();            //$('#BranchAutoId').chosen();            //$('#BranchAutoId').focus();        });        $('#BranchAutoId').change(function () {            get_invoice_list();        });        function get_invoice_list() {            var customer = $('.customerId').val();            var BranchAutoId = $('#BranchAutoId').val();            $('#paid_amount').val('');            if (BranchAutoId == '' || BranchAutoId == 0 || BranchAutoId == null) {            } else if (customer == '' || customer == 0 || customer == null) {            } else {                $("#paid_amount").val('');                $.ajax({                    type: 'POST',                    data: {customer: customer,BranchAutoId:BranchAutoId},                    url: '<?php echo site_url("lpg/SalesController/customer_ajax"); ?>',                    success: function (result) {                        $('#customer_result').html(result);                        $('#voucherheader').show(1000);                        $('#narration').show(1000);                        $('#submitBtn').show(1000);                    }                });            }        }        $('.customerId').change(function () {        });        $('.checkAccountBalance').change(function () {            var accountId = $(this).val();            $.ajax({                type: 'POST',                data: {account: accountId},                url: '<?php echo site_url("lpg/FinaneController/checkBalance"); ?>',                success: function (result) {                    $('#accountBalance').html('');                    $('#accountBalance').html(result);                }            });        });        $("#paid_amount").keyup(function (event) {            var number = $("#customer_mobile").val();            if (event.keyCode == 13) {                adjustamount(number);            }        });        function adjustamount(number) {            //alert('OK');            $('.amount').val('');            var paid_amount = parseFloat($('#paid_amount').val());            $('.ttl_amount').val(paid_amount);            //var advance_amount=parseFloat($('#advance_amount').val());            var advance_amount = 0;            if (isNaN(advance_amount)) {                advance_amount = 0;            }            $.each($('.invoice_id'), function (i, elem) {                var sales_invoice_id = $(this).val();                console.log($(this).val());                var amount = parseFloat($('#dueAmount_' + sales_invoice_id).val());                if (paid_amount > amount && paid_amount > 0) {                    console.log('one');                    $('#paymentAmount_' + sales_invoice_id).val(amount);                } else if (paid_amount < amount && paid_amount > 0) {                    console.log('one 1');                    $('#paymentAmount_' + sales_invoice_id).val(paid_amount);                } else if (paid_amount == amount && paid_amount > 0) {                    console.log('one 2');                    $('#paymentAmount_' + sales_invoice_id).val(paid_amount);                }                paid_amount = paid_amount - amount;            });            if (paid_amount > 0) {                advance_amount + paid_amount;            }        }    });</script><script>    $(document).ready(function () {        $('.date-picker').datepicker({            autoclose: true,            todayHighlight: true        })    });</script>