<div class="row">    <div class="col-md-12">        <div class="portlet box blue">            <div class="portlet-title" style="min-height:21px">                <div class="caption" style="font-size: 14px;padding:1px 0 1px;">                     User List </div>                      </div>                      <div class="portlet-body">                                   <table id="dynamic-table" class="table table-striped table-bordered table-hover">                        <thead>                            <tr>                                <th>Sl</th>                                <th>Name</th>                                <th>Phone</th>                                <th>Email</th>                                <th>Status</th>                                <th>Action</th>                            </tr>                        </thead>                        <tbody>                            <?php                           // dumpVar($userList);                            foreach ($userList as $key => $value): ?>                                <tr>                                    <td><?php echo $key + 1; ?></td>                                    <td><?php echo $value->name; ?></td>                                    <td><?php echo $value->phone; ?></td>                                    <td><?php echo $value->email; ?></td>                                    <td><?php                            if ($value->status == 1):                                    ?>                                            <a href="javascript:void(0)" onclick="userStatusChange('<?php echo $value->admin_id; ?>','2')" class="label label-danger arrowed">                                                <i class="fa fa-ban"></i>                                                Inactive</a>                                        <?php else: ?>                                            <a href="javascript:void(0)" onclick="userStatusChange('<?php echo $value->admin_id; ?>','1')" class="label label-success arrowed">                                                <i class="fa fa-check"></i>                                                Active                                            </a>                                        <?php endif; ?>                                    </td>                                    <td>                                        <div class="btn btn-icon-only blue">                                            <a  href="<?php echo site_url($this->project.'/editUser/' . $value->admin_id); ?>">                                                <i class="fa fa-pencil" style="color:#fff"></i>                                            </a>                                        </div>                                    </td>                                </tr>                            <?php endforeach; ?>                        </tbody>                    </table>                           </div><!-- /.col -->        </div><!-- /.row -->    </div></div><script src="<?php echo base_url('assets/setup.js'); ?>"></script><script type="text/javascript">    function userStatusChange(userId,status){        swal({                title: "Are you sure ?",                text: "You won't be able to revert this!",                showCancelButton: true,                confirmButtonColor: '#73AE28',                cancelButtonColor: '#d33',                confirmButtonText: 'Yes',                cancelButtonText: "No",                closeOnConfirm: true,                closeOnCancel: true,                type: 'warning'            },            function (isConfirm) {                if (isConfirm) {                    var base_u = $('#baseUrl').val();                    var url1='<?php echo site_url()?>/SetupController/userStatusChange';                    $.ajax({                        url: url1,                        type: 'post',                        data: {                            userId: userId,                            status: status                        },                        success: function(data) {                            setTimeout(function(){                                window.location.reload(1);                            }, 100);                            //if(data == 1){                            //    window.location.replace(base_u +"userList");                            //}                        }                    });                }else{                    return false;                }            });    }</script>